svg.script:
//
	var url_parameters=new URL(window.location.toString()).searchParams;
	if(url_parameters.get('v')==1)
	{
		document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0].setAttribute("fill","#1977f3");
	}
	else if(url_parameters.get('v')==2)
	{
		document.getElementsByTagName("svg")[0].appendChild(document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0]);
		document.getElementsByTagName("svg")[0].setAttribute("viewBox","-1 -1 124 124");
		document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0].setAttribute("stroke","#000");
		document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0].setAttribute("stroke-width","1");
		document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0].setAttribute("fill","none");
		document.getElementsByTagName("svg")[0].getElementsByTagName("path")[0].setAttribute("fill","#000");
	}
	else if(url_parameters.get('v')==3)
	{
		document.getElementsByTagName("svg")[0].appendChild(document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0]);
		document.getElementsByTagName("svg")[0].setAttribute("viewBox","-1 -1 124 124");
		document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0].setAttribute("stroke","#1977f3");
		document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0].setAttribute("stroke-width","1");
		document.getElementsByTagName("svg")[0].getElementsByTagName("circle")[0].setAttribute("fill","none");
		document.getElementsByTagName("svg")[0].getElementsByTagName("path")[0].setAttribute("fill","#1977f3");
	}
//
